USE `myblog`;

/*Table structure for table `cms_banner` */

DROP TABLE IF EXISTS `cms_banner`;

CREATE TABLE `cms_banner` (
  `id` bigint(20) NOT NULL COMMENT 'banner表',
  `title` varchar(45) DEFAULT NULL COMMENT '标题',
  `link` varchar(200) DEFAULT NULL COMMENT '连接',
  `image` varchar(45) DEFAULT NULL COMMENT '图片地址',
  `order_num` int(11) DEFAULT NULL COMMENT '排序',
  `type` int(1) DEFAULT NULL COMMENT '模块0首页滚动1新闻页2车辆详情页',
  `status` int(1) DEFAULT NULL COMMENT '状态 0未上线1已上线',
  `create_time` timestamp NULL DEFAULT NULL,
  `update_time` timestamp NULL DEFAULT NULL,
  `oper_id` bigint(20) DEFAULT NULL COMMENT '操作员',
  `terminal` int(1) DEFAULT NULL COMMENT '保留字段(展示终端 1PC 2微信 3电脑和微信)',
  `version` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `cms_banner` */

/*Table structure for table `cms_news` */

DROP TABLE IF EXISTS `cms_news`;

CREATE TABLE `cms_news` (
  `id` bigint(20) NOT NULL COMMENT '政策法规新闻信息表',
  `is_top` int(2) DEFAULT NULL COMMENT '是否置顶 1是 0否',
  `order_num` int(11) DEFAULT NULL COMMENT '排序',
  `is_hot` int(2) DEFAULT NULL COMMENT '热点 1是 0否',
  `type` int(2) DEFAULT NULL COMMENT '新闻类型 1公告 2资讯',
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `introduction` varchar(512) DEFAULT NULL COMMENT '简介',
  `content` mediumtext COMMENT '富文本 内容',
  `pic` varchar(128) DEFAULT NULL COMMENT '标题图',
  `onlinetime` timestamp NULL DEFAULT NULL COMMENT '上线时间',
  `author` varchar(20) DEFAULT NULL COMMENT '作者',
  `status` int(2) DEFAULT NULL COMMENT '是否发布 0未发布 1已发布',
  `update_time` timestamp NULL DEFAULT NULL,
  `create_time` timestamp NULL DEFAULT NULL,
  `oper_id` bigint(20) DEFAULT NULL,
  `version` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `cms_news` */

/*Table structure for table `gather_content` */

DROP TABLE IF EXISTS `gather_content`;

CREATE TABLE `gather_content` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '爬虫收集-新闻信息表',
  `is_top` int(2) DEFAULT NULL COMMENT '是否置顶 1是 0否',
  `order_num` int(11) DEFAULT NULL COMMENT '排序',
  `is_hot` int(2) DEFAULT NULL COMMENT '热点 1是 0否',
  `type` int(2) DEFAULT NULL COMMENT '新闻类型 1公告 2资讯',
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `introduction` varchar(512) DEFAULT NULL COMMENT '简介',
  `content` mediumtext COMMENT '富文本 内容',
  `pic` varchar(128) DEFAULT NULL COMMENT '标题图',
  `onlinetime` timestamp NULL DEFAULT NULL COMMENT '上线时间',
  `author` varchar(20) DEFAULT NULL COMMENT '作者',
  `status` int(2) DEFAULT NULL COMMENT '是否发布 0未发布 1已发布',
  `update_time` timestamp NULL DEFAULT NULL,
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `oper_id` bigint(20) DEFAULT NULL,
  `static_link` varchar(200) DEFAULT NULL COMMENT '静态连接',
  `version` int(11) DEFAULT NULL,
  `his_url` varchar(200) DEFAULT NULL COMMENT '原网页路径',
  `his_img` varchar(200) DEFAULT NULL COMMENT '原网页新闻标题图片',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=497 DEFAULT CHARSET=utf8;

/*Data for the table `gather_content` */

insert  into `gather_content`(`id`,`is_top`,`order_num`,`is_hot`,`type`,`title`,`introduction`,`content`,`pic`,`onlinetime`,`author`,`status`,`update_time`,`create_time`,`oper_id`,`static_link`,`version`,`his_url`,`his_img`) values (141,NULL,NULL,NULL,NULL,'第二届单向街·书店文学奖颁奖典礼落幕',NULL,'<p class=\"titdd-Article\">[<strong>摘要</strong>]本届书店文学奖邀请多位国内知名文化学者担任评委，共评选出了12 本年度书籍和 5 个特别大奖。</p>\n<p style=\"TEXT-INDENT: 2em\">从2016年最后一个月开始，在为期一个半月的时间里，单向空间向文学、艺术、电影、时尚、设计等领域的个人与组织发出邀约，邀约最富活力的创作者，举办不同维度的跨界讨论。最终在1月14日，凝聚成本场华彩乐章——第二届单向街·书店文学奖的颁奖礼。这场典礼在位于北京三里屯的CHAO举行。\n <!--?xml:namespace prefix = \"o\" /-->\n <o:p></o:p>\n <o:p> \n </o:p></p>\n<p style=\"TEXT-ALIGN: center\"><img alt=\"第二届单向街·书店文学奖颁奖典礼落幕\" src=\"http://img1.gtimg.com/news/pics/hv1/40/179/2179/141735160.png\"></p>\n<p style=\"TEXT-ALIGN: center\">图1：会场全貌</p>\n<p style=\"TEXT-INDENT: 2em\">▍一家书店，一个文化地标，一个文学奖，一场文化盛宴\n <o:p></o:p>\n <o:p></o:p></p>\n<p style=\"TEXT-INDENT: 2em\">这是一次星光熠熠的颁奖礼，贾樟柯、陈冠中、张永和、止庵、史航、刘苏里、骆以军、刘瑞琳、阿乙、钟立风、张定浩等文化名流都莅临会场。单向空间希望通过文学奖，承担书店在公共领域应该承担的角色，同读者们一同寻找这个时代的独特表达，理解他者的世界，从而通过亲眼观察，用中文阅读与写作进入另一个世界。\n <o:p></o:p>\n <o:p> \n </o:p></p>\n<p style=\"TEXT-ALIGN: center\"><img alt=\"第二届单向街·书店文学奖颁奖典礼落幕\" src=\"http://img1.gtimg.com/news/pics/hv1/46/179/2179/141735166.jpg\"></p>\n<p style=\"TEXT-ALIGN: center\">图2：场外：单读MOOK在颁奖典礼现场外呈队列迎接来宾</p>\n<p style=\"TEXT-INDENT: 2em\">这个奖项的评选范围涵盖年度书籍、青年作家、文学翻译、旅行写作、年度编辑、年度新声……提名名单由100家中国本土书店提名候选，由权威学者、作家、艺术家、建筑师联合终审：西川、阎连科、戴锦华、贾樟柯、刘小东、张永和、叶锦添、刘苏里、陈冠中、许知远、止庵、史航，他们一直在行业中保持着最前沿的敏锐，最终评选出华语出版界在过去一年中最值得关注的声音。\n <o:p></o:p>\n <o:p> \n </o:p></p>\n<p align=\"center\"><img alt=\"第二届单向街·书店文学奖颁奖典礼落幕\" src=\"http://img1.gtimg.com/news/pics/hv1/230/179/2179/141735350.jpg\"></p>\n<p style=\"TEXT-ALIGN: center\">图3：坐在观众席中的嘉宾在聆听颁奖礼</p>\n<p style=\"TEXT-INDENT: 2em\">不论今天的你是一位文学门外汉，时代的冷眼旁观者，还是激情澎湃的文艺青年、资深的阅读或写作者，都能在这次颁奖中获得滋养：“获奖者”中有网络时代重新用技术结构与重构内容的媒体，也有一直以来不放弃文学交流的译者，更有最优秀的青年作家，他们笔下的世界，也许就是我们明天的世界。\n <o:p></o:p>\n <o:p></o:p></p>\n<p style=\"TEXT-INDENT: 2em\">许知远在颁奖典礼“开篇”的演讲中着重讲述了创建书店文学奖的初衷，他说：“我们遇到很多危机，道德上的危机，智识上的危机，价值上的危机……我们在生活中多大程度上用自己的制式来回应时代？”\n <o:p></o:p>\n <o:p></o:p></p>\n<p style=\"TEXT-INDENT: 2em\">因参与评奖过程，他感受颇多：他这次评奖中，发现很多生于80、90年代的优秀青年，他希望这些青年能够真正打破前辈们的范式，寻找一种新的方式，就如当年贾樟柯的青年影像中，一群年轻人把瓶子摔在地上，打破封闭，打破领域的疆界，“倘若想追求伟大的创造力和新的精神，所有的境界必须沟通、争吵、辩论、激发产生的。我们希望这样小小的东西，能够变成能够汇集更多声音的东西。\n <o:p> \n </o:p></p>\n<p style=\"TEXT-ALIGN: center\"><img alt=\"第二届单向街·书店文学奖颁奖典礼落幕\" src=\"http://img1.gtimg.com/news/pics/hv1/53/179/2179/141735173.jpg\"></p>\n<p style=\"TEXT-ALIGN: center\">图4：许知远在演讲中</p>\n<p style=\"TEXT-INDENT: 2em\">▍12本书籍，5个特别大奖\n <o:p></o:p>\n <o:p></o:p></p>\n<p style=\"TEXT-INDENT: 2em\">颁奖典礼首先颁出的大奖是年度书籍奖。他们是：《二手时间》、《北鸢》、《茧》、《慈悲》、《伦敦传》 、《罗曼蒂克消亡史》、《想象共和国》、《我悲哀地望着我们这一代人》、《第二本书》、《西班牙内战》、《入世哲学家》、《在缅甸寻找乔治·奥威尔》。你在这些书中，可以看到个人家族史、迷惘的青春、逝去的时代、美国与民主…….总之，你可以在这里寻找到我们这个时代思想的强音。\n <o:p></o:p>\n <o:p></o:p></p>\n<p style=\"TEXT-INDENT: 2em\">颁出的是首个特别大奖——年度新声奖。这一奖项专注发现以新媒体形式重新突围的内容创作团队。他们专注各自的领域，为读者持续生产高质量的内容。2016年的年度新声奖颁给了正午故事。\n <o:p></o:p>\n <o:p></o:p></p>\n<p style=\"TEXT-INDENT: 2em\">正午主编谢丁先生上台领奖，他说：“传统媒体是不行了，正午做的是一新的东西，提供的是新的可能性，我们和传统媒体不一样，角度不一样，所以奖才颁给我们。”\n <o:p></o:p>\n <o:p> \n </o:p></p>\n<p style=\"TEXT-ALIGN: center\"><img alt=\"第二届单向街·书店文学奖颁奖典礼落幕\" src=\"http://img1.gtimg.com/news/pics/hv1/55/179/2179/141735175.jpg\"></p>\n<p style=\"TEXT-ALIGN: center\">图5：正午主编谢丁在发言中</p>\n<p style=\"TEXT-INDENT: 2em\">\n <o:p></o:p></p>\n<p style=\"TEXT-INDENT: 2em\">\n <o:p></o:p>谢丁发言的语调风趣幽默，但是他半开玩笑的所谓“传统媒体已经不行了”，引得全场连连发笑。\n <o:p></o:p>\n <o:p></o:p></p>\n<p style=\"TEXT-INDENT: 2em\">设置年度编辑奖，是因为单向空间相信，每一本书背后，都凝聚着一位编辑的独特眼光与专业能力。\n <o:p></o:p>\n <o:p> \n </o:p></p>\n<p style=\"TEXT-ALIGN: center\"><img alt=\"第二届单向街·书店文学奖颁奖典礼落幕\" src=\"http://img1.gtimg.com/news/pics/hv1/64/179/2179/141735184.jpg\"></p>\n<p style=\"TEXT-ALIGN: center\">图6:彭伦在发言中</p>\n<p style=\"TEXT-INDENT: 2em\">最终的获奖人是彭伦，他说：“作为编辑不必要站下聚光灯下，而是希望自己书的作者站在聚光灯下。”他特别向大家介绍了一本名为《天才的编辑》的书。他说这本书是美国图书编辑麦克斯·珀金斯的传记，作为编辑，他是可能是无法超越的：“我作为编辑，也是作为这本书的译者，我希望它可以成为出版界同行都能学习的楷模，所以我特别把这本书带上来。”\n <o:p></o:p>\n <o:p> \n </o:p></p>\n<p style=\"TEXT-ALIGN: center\"><img alt=\"第二届单向街·书店文学奖颁奖典礼落幕\" src=\"http://img1.gtimg.com/news/pics/hv1/68/179/2179/141735188.jpg\"></p>\n<p style=\"TEXT-ALIGN: center\">图7：《天才的编辑》封面</p>\n<p style=\"TEXT-INDENT: 2em\">设置年度文学翻译奖，是基于如今越来越多的译文作品进入国内，翻译依然是我们从文字中获得灵感的重要一环。单向空间希望借这个奖，号召全国书店和我们一起来提名今年为大家带来了优秀译文作品的译者。最终这一奖项授予了陆大鹏先生 。</p>\n<p style=\"TEXT-ALIGN: center\"><img alt=\"第二届单向街·书店文学奖颁奖典礼落幕\" src=\"http://img1.gtimg.com/news/pics/hv1/71/179/2179/141735191.jpg\"></p>\n<p style=\"TEXT-ALIGN: center\">图8：陆大鹏上台领奖致辞</p>\n<p style=\"TEXT-INDENT: 2em\">年度文学翻译奖颁奖结束后，颁发的是年度旅行写作奖。这一奖项是为了支持中文作者在阅读世界的路上走向更远的旅程。最终，将这一奖项授予了陈丹燕女士。\n <o:p></o:p>\n <o:p> \n </o:p></p>\n<p style=\"TEXT-ALIGN: center\"><img alt=\"第二届单向街·书店文学奖颁奖典礼落幕\" src=\"http://img1.gtimg.com/news/pics/hv1/73/179/2179/141735193.jpg\"></p>\n<p style=\"TEXT-ALIGN: center\">图9：陈丹燕致辞</p>\n<p style=\"TEXT-INDENT: 2em\">陈丹燕是当代都市文化的代言人。她也是中国作家中第一个走出国门的背包客，\n <o:p></o:p>\n <o:p></o:p></p>\n<p style=\"TEXT-INDENT: 2em\">年度青年作家是这五项特别大奖中最重要的奖项。这一奖项鼓励新的作者持续为汉语写作注入时代气息。他们是整个社会中最敏锐的感受者，发出面向未来的声音。李静睿女士斩获此大奖。\n <o:p></o:p>\n <o:p></o:p></p>\n<p style=\"TEXT-INDENT: 2em\">李静睿在 2016 年出版了一本名为《微小的命运》的书，讲述了一个三十岁女孩儿的双重生活。李静睿是一位安静的写作者，温柔地经营着语言，勇敢地积蓄着能量。在这个全球化、民族主义等概念滥觞的年代里，李静睿创造着一个理想的自己。\n <o:p></o:p>\n <o:p> \n </o:p></p>\n<p style=\"TEXT-ALIGN: center\"><img alt=\"第二届单向街·书店文学奖颁奖典礼落幕\" src=\"http://img1.gtimg.com/news/pics/hv1/84/179/2179/141735204.jpg\"></p>\n<p style=\"TEXT-ALIGN: center\">图10：李静睿致辞</p>\n<p style=\"TEXT-INDENT: 2em\">此时，颁奖礼已经接近尾声，贾樟柯先生此刻登台，发表了主题演讲——他谈到生活中的变与不变。他谈到今天中国人，在过去30年一直经历着变化。然而却一直面对着“生命是什么”这种终极性问题。他一直在感知新的时代，但在感知中也回想起古人到今人的经历：\n <o:p></o:p></p>\n<p style=\"TEXT-INDENT: 2em\">“为什么我们那么的不适应地球，我们必须用熟的食物，我们对四季寒冷炎热的抵抗力非常弱，几乎每个人都会得慢性病，我们其实展现了对地球强大的不适应力……”</p>\n<p style=\"TEXT-INDENT: 2em\">文学奖虽然关注作家、书籍、编辑、译者，但本质是在关注生命。\n <o:p></o:p>\n <o:p></o:p></p>\n<p style=\"TEXT-INDENT: 2em\">▍一本书，一个人，一个火种\n <o:p></o:p>\n <o:p></o:p></p>\n<p style=\"TEXT-INDENT: 2em\">在颁奖礼中间，还有一个论坛，嘉宾有阿乙、陈冠中、史航、王晓渔，论坛由《单读》主编吴琦主持。嘉宾们的妙语在观众中掀起了小高潮。他们都谈到书店在他们生命中扮演的角色。比如陈冠中便谈到读者走入一家书店，可以很随机的改变自己的知识结构。可是现在这样的传统好书店越来越少，人们已经渐渐习惯于网络上的推送，而失去了与智慧与知识相遇的机会。\n <o:p></o:p>\n <o:p></o:p>\n <o:p> \n </o:p></p>\n<p style=\"TEXT-ALIGN: center\"><img alt=\"第二届单向街·书店文学奖颁奖典礼落幕\" src=\"http://img1.gtimg.com/news/pics/hv1/86/179/2179/141735206.jpg\"></p>\n<p style=\"TEXT-ALIGN: center\">图11：从左至右依次为：吴琦、阿乙、陈冠中、王晓渔、史航</p>\n<p style=\"TEXT-INDENT: 2em\">这些嘉宾都谈到了这个时代中的焦虑，去书店便是消解这种焦虑的方法之一。就如主持人沈星形容的在场的每一个人都是一个火种一样，书店里的每一本书都是一个火种。</p>\n<p style=\"TEXT-INDENT: 2em\">许知远在开头的演讲中也谈到了黑暗、光亮，他说：“其实在黑暗的时代中，我们也有权去期待一种启明，这种启明并不来自理论和概念，而更多来自于不确定的、闪烁而又经常很微弱的光亮。这光亮源于某些男人和女人，源于他们的生命，它们在几乎所有情况下都点燃着，并把光散射到他们在尘世所拥有的生命所及的全部范围。我们希望更多男人、女人的出现，更多作品的出现，可以一起走下去，让光亮变得越来越闪亮起来。”<a id=\"backqqcom\" title=\"点击进入腾讯首页\" style=\"WHITE-SPACE: nowrap\" target=\"_blank\" href=\"http://www.qq.com/?pref=article\"></a></p>',NULL,NULL,NULL,NULL,NULL,'2017-08-13 11:40:24',NULL,NULL,NULL,'http://news.qq.com/a/20170115/012771.htm',NULL),(142,NULL,NULL,NULL,NULL,'四分钟速览《将改革进行到底》第二集《引领经济发展新常态》',NULL,'<!-- 相关视频 --> \n<!-- built at: 2017-7-13 18:06:20 --> \n<script type=\"text/javascript\">\n    var related_video_info = {\n        vid: \'a05260qi5gc\',\n        cid: \'3asm5bp5h9mdtfk\',\n        url: \'http://v.qq.com/page/a/0/c/a05260qi5gc.html\',\n        pic: \'http://inews.gtimg.com/newsapp_ls/0/1813549948_640480/0\',\n        title: \'四分钟速览《将改革进行到底》第二集《引领经济发展新常态》\'\n    };\n</script> \n<style type=\"text/css\">.rv-root-v2{font-family:\"\\5FAE\\8F6F\\96C5\\9ED1\",sans-serif;color:#000}.rv-root-v2 a{text-decoration:none;color:#000}.rv-root-v2 h1,.rv-root-v2 h2,.rv-root-v2 h3,.rv-root-v2 ol,.rv-root-v2 p,.rv-root-v2 ul{display:block;padding:0;margin:0}.rv-root-v2 img{border:0}.rv-root-v2 .rv-cf:after,.rv-root-v2 .rv-cf:before{content:\" \";display:table}.rv-root-v2 .rv-cf:after{clear:both}.rv-root-v2 .rv-cf{*zoom:1}.rv-root-v2 .rv-top{height:40px;line-height:40px;background-color:#363636}.rv-root-v2 .rv-top .rv-fold-btn{float:right;padding-left:20px;ma