/*Table structure for table `sys_rule_privilege` */

DROP TABLE IF EXISTS `sys_rule_privilege`;

CREATE TABLE `sys_rule_privilege` (
  `id` bigint(20) NOT NULL COMMENT '主键-角色功能对应表',
  `privilege_master` int(11) DEFAULT NULL COMMENT '分配类型(1=用户,2是角色)',
  `privilege_id` bigint(20) DEFAULT NULL COMMENT '分配类型对应id',
  `rule_id` bigint(20) DEFAULT NULL COMMENT '权限类型id',
  `is_enable` smallint(6) DEFAULT NULL COMMENT '是否启用',
  `create_time` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `create_user` bigint(20) DEFAULT NULL COMMENT '创建用户',
  `update_time` timestamp NULL DEFAULT NULL COMMENT '修改时间',
  `update_user` bigint(20) DEFAULT NULL COMMENT '修改用户',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `sys_rule_privilege` */

/*Table structure for table `sys_rule_role` */

DROP TABLE IF EXISTS `sys_rule_role`;

CREATE TABLE `sys_rule_role` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `role_name` varchar(20) DEFAULT NULL COMMENT '角色名',
  `role_desc` varchar(50) DEFAULT NULL COMMENT '描叙',
  `create_user` bigint(20) DEFAULT NULL COMMENT '创建用户',
