  `update_user` bigint(20) DEFAULT NULL COMMENT '修改用户',
  `update_time` timestamp NULL DEFAULT NULL COMMENT '修改时间',
  `is_enable` int(1) DEFAULT '1' COMMENT '是否启用0未启用1启用',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

