/*Table structure for table `sys_scheduled` */

DROP TABLE IF EXISTS `sys_scheduled`;

CREATE TABLE `sys_scheduled` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `job_name` varchar(64) DEFAULT NULL COMMENT 'job名称，表示每日重复执行的job，例如充值对账',
  `job_start` timestamp NULL DEFAULT NULL COMMENT 'job开始时间',
  `job_end` timestamp NULL DEFAULT NULL COMMENT 'job结束时间',
  `job_interval` bigint(20) DEFAULT NULL COMMENT '单位为毫秒,job经历的时间',
  `job_status` int(1) DEFAULT NULL COMMENT '0:初始化状态，1:job执行中，2job执行成功，3 job执行失败',
  `job_biz_name` varchar(128) DEFAULT NULL COMMENT 'job业务名包括执行的时间比如：20140623-充值对账',
  `job_biz_day` varchar(45) DEFAULT NULL COMMENT 'job执行日格式yyyymmdd',
  `job_version` int(4) DEFAULT NULL COMMENT '乐观锁控制',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `sys_scheduled` */

/*Table structure for table `sys_user` */

DROP TABLE IF EXISTS `sys_user`;

CREATE TABLE `sys_user` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '后台用户',
  `account` varchar(255) DEFAULT NULL COMMENT '账户',
  `password` varchar(255) DEFAULT NULL COMMENT '密码',
  `real_name` varchar(255) DEFAULT NULL COMMENT '真实姓名',
  `position` varchar(100) DEFAULT NULL COMMENT '职位',
  `email` varchar(128) DEFAULT NULL COMMENT '邮箱',
  `age` int(3) DEFAULT NULL COMMENT '年龄 保密-1',
  `is_true` int(10) NOT NULL DEFAULT '1' COMMENT '是否启用0不允许登陆1允许登陆',
  `telephone` varchar(11) DEFAULT NULL COMMENT '电话',
  `img` varchar(200) DEFAULT NULL COMMENT '头像',
  `is_delete` int(1) NOT NULL DEFAULT '0' COMMENT '0未删除1删除',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `update_time` timestamp NULL DEFAULT NULL,
  `version` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=29 DEFAULT CHARSET=utf8;

