/*Table structure for table `t_music` */

DROP TABLE IF EXISTS `t_music`;

CREATE TABLE `t_music` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `music_name` varchar(100) DEFAULT NULL COMMENT '音乐名称',
  `music_auther` varchar(100) DEFAULT NULL COMMENT '歌手名称',
  `music_album` varchar(100) DEFAULT NULL COMMENT '专辑名称',
  `music_lyric` text COMMENT '歌词',
  `music_img` varchar(100) DEFAULT NULL COMMENT '歌曲图片',
  `music_type` int(11) DEFAULT NULL COMMENT '歌曲类型',
  `music_src` varchar(100) DEFAULT NULL COMMENT '歌曲路径',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `update_time` timestamp NULL DEFAULT NULL,
  `is_delete` int(11) DEFAULT '0' COMMENT '0:未删除1删除',
  `click_amount` bigint(20) DEFAULT NULL COMMENT '点击量',
  `type` int(11) DEFAULT NULL COMMENT '预留字段',
  `is_state` int(1) DEFAULT '0' COMMENT '0:正常1未发布',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

