/*Table structure for table `t_users` */

DROP TABLE IF EXISTS `t_users`;

CREATE TABLE `t_users` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '用户表',
  `name` varchar(64) NOT NULL COMMENT '姓名',
  `phone` varchar(16) DEFAULT NULL COMMENT '手机',
  `email` varchar(128) DEFAULT NULL COMMENT '邮箱',
  `pwd` varchar(128) NOT NULL COMMENT '密码',
  `account` varchar(64) DEFAULT NULL COMMENT '账号名',
  `status` int(2) DEFAULT NULL COMMENT '0待审核,1可用,2审核不通过,3冻结',
  `is_delete` int(1) NOT NULL DEFAULT '0' COMMENT '0未删除1删除',
  `create_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `update_date` timestamp NULL DEFAULT NULL,
  `img` varchar(200) DEFAULT NULL COMMENT '头像',
  `autograph` varchar(255) DEFAULT NULL COMMENT '签名',
  `nickname` varchar(255) DEFAULT NULL COMMENT '昵称',
  `sex` int(1) DEFAULT NULL COMMENT '性别0女1男3保密',
  `birthday` datetime DEFAULT NULL COMMENT '生日',
  `occupation` varchar(200) DEFAULT NULL COMMENT '职业',
  `company` varchar(255) DEFAULT NULL COMMENT '公司',
  `location` varchar(255) DEFAULT NULL COMMENT '所在地',
  `hometown` varchar(255) DEFAULT NULL COMMENT '家乡',
  `personal_note` text COMMENT '个人说明',
  `age` int(3) DEFAULT NULL COMMENT '年龄',
  `type` int(2) DEFAULT NULL COMMENT '预留字段',
  `version` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

