mapper.select:
select 
count(1)
from gather_content
where 1=1

	onlinetime IS NOT NULL


	and type = #{type}


	and is_hot = #{isHot}


	and is_top = #{isTop}


	and create_time >= #{start}


	and create_time <= #{end}

select 

from gather_content
where 1=1

	onlinetime IS NOT NULL


	and type = #{type}


	and is_hot = #{isHot}


	and is_top = #{isTop}


	and create_time >= #{start}


	and create_time <= #{end}

order by create_time desc,update_time desc
LIMIT #{pageStartNumber},#{pageEndNumber}
